mapper.update:
UPDATE t_animation
SET hit = hit + 1
WHERE title = #{title}

mapper.select:
SELECT COALESCE(AVG(grade), 0) AS t_grade
FROM t_grade
WHERE ani_idx = #{ani_idx}

mapper.delete:
DELETE FROM t_anilike
WHERE ani_idx = #{ani_idx}
AND useridx = #{useridx}

mapper.select:
SELECT COUNT(*) > 0
FROM t_anilike
WHERE ani_idx = #{ani_idx} AND useridx = #{useridx}
